import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [inputImage, setInputImage] = useState(null);
    const [outputImage, setOutputImage] = useState(null);

    const handleImageChange = (e) => {
        setInputImage(e.target.files[0]);
        setOutputImage(null); // Reset
    };

    const handleSubmit = async(e) => {
        e.preventDefault();
        if (!inputImage) return;

        const formData = new FormData();
        formData.append('image', inputImage);

        try {
            const res = await axios.post('http://localhost:5000/process', formData, {
                responseType: 'blob',
            });
            setOutputImage(URL.createObjectURL(res.data));
        } catch (err) {
            console.error(err);
            alert("Failed to process image.");
        }
    };

    return ( <
        div className = "min-h-screen flex flex-col items-center justify-center p-4 bg-gray-100" >
        <
        h1 className = "text-3xl font-bold mb-4" > Deep Learning Image Processor < /h1>

        <
        form onSubmit = { handleSubmit }
        className = "mb-6" >
        <
        input type = "file"
        accept = "image/*"
        onChange = { handleImageChange }
        /> <
        button type = "submit"
        className = "ml-4 px-4 py-2 bg-blue-500 text-white rounded" > Process < /button> <
        /form>

        {
            inputImage && ( <
                div className = "flex gap-8" >
                <
                div >
                <
                h2 className = "text-xl mb-2" > Input Image < /h2> <
                img src = { URL.createObjectURL(inputImage) }
                alt = "Input"
                className = "max-w-sm" / >
                <
                /div> {
                    outputImage && ( <
                        div >
                        <
                        h2 className = "text-xl mb-2" > Output Image < /h2> <
                        img src = { outputImage }
                        alt = "Output"
                        className = "max-w-sm" / >
                        <
                        /div>
                    )
                } <
                /div>
            )
        } <
        /div>
    );
}

export default App;
