import React, { useRef, useState } from 'react'

const kernels = {
  Identity: {
    matrix: [
      [0, 0, 0],
      [0, 1, 0],
      [0, 0, 0],
    ],
  },
  BoxBlur: {
    matrix: [
      [1 / 9, 1 / 9, 1 / 9],
      [1 / 9, 1 / 9, 1 / 9],
      [1 / 9, 1 / 9, 1 / 9],
    ],
  },
  GaussianBlur: {
    matrix: [
      [1 / 16, 2 / 16, 1 / 16],
      [2 / 16, 4 / 16, 2 / 16],
      [1 / 16, 2 / 16, 1 / 16],
    ],
  },
  Sharpen: {
    matrix: [
      [0, -1, 0],
      [-1, 5, -1],
      [0, -1, 0],
    ],
  },
  EdgeDetection: {
    matrix: [
      [1, 0, -1],
      [0, 0, 0],
      [-1, 0, 1],
    ],
  },
  Emboss: {
    matrix: [
      [-2, -1, 0],
      [-1, 1, 1],
      [0, 1, 2],
    ],
  },
}

const  KernelExplorer = () => {
  const originalCanvasRef = useRef()
  const filteredCanvasRef = useRef()
  const fileInputRef = useRef()
  const [image, setImage] = useState(null)
  const [kernel, setKernel] = useState('Identity')

  const handleImageUpload = (event) => {
    const file = event.target.files[0]
    const reader = new FileReader()
    reader.onload = () => {
      const img = new Image()
      img.src = reader.result
      img.onload = () => {
        setImage(img)
        drawImage(img)
      }
    }
    if (file) {
      reader.readAsDataURL(file)
    }
  }

  const drawImage = (img) => {
    const canvas = originalCanvasRef.current
    const ctx = canvas.getContext('2d')
    canvas.width = img.width
    canvas.height = img.height
    ctx.drawImage(img, 0, 0)
  }

  const applyConvolution = () => {
    if (!image) return

    const inputCanvas = originalCanvasRef.current
    const outputCanvas = filteredCanvasRef.current
    const ctx = inputCanvas.getContext('2d')
    const outputCtx = outputCanvas.getContext('2d')

    const { width, height } = inputCanvas
    const imageData = ctx.getImageData(0, 0, width, height)
    const outputData = outputCtx.createImageData(width, height)

    const matrix = kernels[kernel].matrix
    const side = 3
    const halfSide = Math.floor(side / 2)

    const getPixel = (x, y, c) => {
      const i = (y * width + x) * 4 + c
      return imageData.data[i]
    }

    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {
        for (let c = 0; c < 3; c++) {
          let pixel = 0
          for (let ky = 0; ky < side; ky++) {
            for (let kx = 0; kx < side; kx++) {
              const px = x + kx - halfSide
              const py = y + ky - halfSide
              if (px >= 0 && px < width && py >= 0 && py < height) {
                pixel += getPixel(px, py, c) * matrix[ky][kx]
              }
            }
          }
          const i = (y * width + x) * 4 + c
          outputData.data[i] = Math.min(255, Math.max(0, pixel))
        }
        outputData.data[(y * width + x) * 4 + 3] = 255 // alpha
      }
    }

    outputCanvas.width = width
    outputCanvas.height = height
    outputCtx.putImageData(outputData, 0, 0)
  }

  return (
    <div style={{ padding: 30, fontFamily: 'Arial' }}>
      <h1>ðŸ§  Image Kernel Visualizer</h1>
      <p>Upload an image and apply different convolution filters.</p>
      <input
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
        ref={fileInputRef}
        style={{ marginBottom: 20 }}
      />

      <div style={{ marginBottom: 20 }}>
        <label htmlFor="kernel">Choose a Kernel: </label>
        <select
          id="kernel"
          value={kernel}
          onChange={(e) => setKernel(e.target.value)}
        >
          {Object.keys(kernels).map((key) => (
            <option key={key} value={key}>
              {key}
            </option>
          ))}
        </select>

        <button
          onClick={applyConvolution}
          style={{ marginLeft: 10, padding: '6px 12px' }}
        >
          Apply
        </button>
      </div>

      <div style={{ display: 'flex', gap: 20 }}>
        <div>
          <h3>Original Image</h3>
          <canvas ref={originalCanvasRef} style={{ border: '1px solid #ccc' }} />
        </div>
        <div>
          <h3>Filtered Image</h3>
          <canvas ref={filteredCanvasRef} style={{ border: '1px solid #ccc' }} />
        </div>
      </div>
    </div>
  )
}

export default  KernelExplorer;
