import React, { useRef, useState } from 'react';

const kernels = {
  Identity: {
    matrix: [
      [0, 0, 0],
      [0, 1, 0],
      [0, 0, 0],
    ],
  },
  BoxBlur: {
    matrix: [
      [1 / 9, 1 / 9, 1 / 9],
      [1 / 9, 1 / 9, 1 / 9],
      [1 / 9, 1 / 9, 1 / 9],
    ],
  },
  GaussianBlur: {
    matrix: [
      [1 / 16, 2 / 16, 1 / 16],
      [2 / 16, 4 / 16, 2 / 16],
      [1 / 16, 2 / 16, 1 / 16],
    ],
  },
  Sharpen: {
    matrix: [
      [0, -1, 0],
      [-1, 5, -1],
      [0, -1, 0],
    ],
  },
  EdgeDetection: {
    matrix: [
      [1, 0, -1],
      [0, 0, 0],
      [-1, 0, 1],
    ],
  },
  Emboss: {
    matrix: [
      [-2, -1, 0],
      [-1, 1, 1],
      [0, 1, 2],
    ],
  },
};

const  KernelExplorer= () => {
  const originalCanvasRef = useRef();
  const filteredCanvasRef = useRef();
  const fileInputRef = useRef();
  const [image, setImage] = useState(null);
  const [kernel, setKernel] = useState('Identity');
  const [message, setMessage] = useState('Click on the image to apply kernel at that pixel');

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = () => {
      const img = new Image();
      img.src = reader.result;
      img.onload = () => {
        setImage(img);
        drawImage(img);
        clearFilteredCanvas(img.width, img.height);
      };
    };
    if (file) {
      reader.readAsDataURL(file);
    }
  };

  const drawImage = (img) => {
    const canvas = originalCanvasRef.current;
    const ctx = canvas.getContext('2d');
    canvas.width = img.width;
    canvas.height = img.height;
    ctx.drawImage(img, 0, 0);
  };

  const clearFilteredCanvas = (w, h) => {
    const canvas = filteredCanvasRef.current;
    canvas.width = w;
    canvas.height = h;
    const ctx = canvas.getContext('2d');
    ctx.fillStyle = 'black';
    ctx.fillRect(0, 0, w, h);
  };

  const handleCanvasClick = (e) => {
    if (!image) return;

    const canvas = originalCanvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = Math.floor(e.clientX - rect.left);
    const y = Math.floor(e.clientY - rect.top);
    setMessage(`Applied kernel at (${x}, ${y})`);

    applyConvolutionAtPoint(x, y);
  };

  const applyConvolutionAtPoint = (x, y) => {
    const inputCanvas = originalCanvasRef.current;
    const outputCanvas = filteredCanvasRef.current;
    const inputCtx = inputCanvas.getContext('2d');
    const outputCtx = outputCanvas.getContext('2d');

    const { width, height } = inputCanvas;
    const imageData = inputCtx.getImageData(0, 0, width, height);
    const outputData = outputCtx.createImageData(width, height);

    const matrix = kernels[kernel].matrix;
    const side = 3;
    const halfSide = Math.floor(side / 2);

    const getPixel = (x, y, c) => {
      const i = (y * width + x) * 4 + c;
      return imageData.data[i];
    };

    const setPixel = (x, y, r, g, b) => {
      const i = (y * width + x) * 4;
      outputData.data[i] = r;
      outputData.data[i + 1] = g;
      outputData.data[i + 2] = b;
      outputData.data[i + 3] = 255;
    };

    for (let j = -halfSide; j <= halfSide; j++) {
      for (let i = -halfSide; i <= halfSide; i++) {
        const cx = x + i;
        const cy = y + j;
        if (cx < 0 || cy < 0 || cx >= width || cy >= height) continue;

        let r = 0,
          g = 0,
          b = 0;
        for (let ky = 0; ky < side; ky++) {
          for (let kx = 0; kx < side; kx++) {
            const px = cx + kx - halfSide;
            const py = cy + ky - halfSide;
            if (px >= 0 && px < width && py >= 0 && py < height) {
              const weight = matrix[ky][kx];
              r += getPixel(px, py, 0) * weight;
              g += getPixel(px, py, 1) * weight;
              b += getPixel(px, py, 2) * weight;
            }
          }
        }

        setPixel(cx, cy, Math.min(255, Math.max(0, r)), Math.min(255, Math.max(0, g)), Math.min(255, Math.max(0, b)));
      }
    }

    outputCtx.putImageData(outputData, 0, 0);
  };

  return (
    <div style={{ padding: 30, fontFamily: 'Arial' }}>
      <h1>ðŸŽ¯ Selective Image Kernel Visualizer</h1>
      <p>Upload an image, pick a kernel, and click on a pixel to apply it only at that location!</p>

      <input
        type="file"
        accept="image/*"
        onChange={handleImageUpload}
        ref={fileInputRef}
        style={{ marginBottom: 20 }}
      />

      <div style={{ marginBottom: 10 }}>
        <label htmlFor="kernel">Choose Kernel: </label>
        <select
          id="kernel"
          value={kernel}
          onChange={(e) => setKernel(e.target.value)}
        >
          {Object.keys(kernels).map((key) => (
            <option key={key} value={key}>
              {key}
            </option>
          ))}
        </select>
      </div>

      <p style={{ color: 'gray' }}>{message}</p>

      <div style={{ display: 'flex', gap: 20 }}>
        <div>
          <h3>Original Image</h3>
          <canvas
            ref={originalCanvasRef}
            style={{ border: '1px solid #ccc', cursor: 'crosshair' }}
            onClick={handleCanvasClick}
          />
        </div>
        <div>
          <h3>Filtered Output (Selected Region Only)</h3>
          <canvas
            ref={filteredCanvasRef}
            style={{ border: '1px solid #ccc' }}
          />
        </div>
      </div>
    </div>
  );
};

export default KernelExplorer;
