import React, { useState, useEffect } from 'react';
import './FeatureMaps.css';

function FeatureMaps() {
  const [models, setModels] = useState({});
  const [selectedModel, setSelectedModel] = useState('');
  const [selectedLayer, setSelectedLayer] = useState('');
  const [includeHeatmap, setIncludeHeatmap] = useState(true);
  const [image, setImage] = useState(null);
  const [previewImage, setPreviewImage] = useState(null);
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState({
    models: true,
    processing: false
  });
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchModels = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/models');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setModels(data);
        setLoading(prev => ({ ...prev, models: false }));
      } catch (err) {
        console.error('Error fetching models:', err);
        setError('Failed to load models. Please try again later.');
        setLoading(prev => ({ ...prev, models: false }));
      }
    };

    fetchModels();
  }, []);

  const handleModelChange = (e) => {
    setSelectedModel(e.target.value);
    setSelectedLayer('');
    setResults(null); // Clear previous results when model changes
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setImage(file);
      
      // Create preview
      const reader = new FileReader();
      reader.onload = (event) => {
        setPreviewImage(event.target.result);
      };
      reader.readAsDataURL(file);
      
      setResults(null); // Clear previous results when new image is selected
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!selectedModel || !selectedLayer || !image) {
      setError('Please select a model, layer, and image');
      return;
    }

    setLoading(prev => ({ ...prev, processing: true }));
    setError('');

    try {
      const formData = new FormData();
      formData.append('model', selectedModel);
      formData.append('layer', selectedLayer);
      formData.append('heatmap', includeHeatmap);
      formData.append('file', image);

      const response = await fetch('http://localhost:5000/api/process', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Processing failed');
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      console.error('Processing error:', err);
      setError(err.message || 'An error occurred during processing');
    } finally {
      setLoading(prev => ({ ...prev, processing: false }));
    }
  };

  if (loading.models) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>Loading models...</p>
      </div>
    );
  }

  if (error && !loading.models) {
    return (
      <div className="error-container">
        <p className="error-message">{error}</p>
        <button onClick={() => window.location.reload()}>Try Again</button>
      </div>
    );
  }

  return (
    <div className="feature-maps-container">
      <header className="header">
        <h1>CNN Feature Maps Visualizer</h1>
        <p>Visualize convolutional layers and Grad-CAM heatmaps from popular CNN models</p>
      </header>

      <div className="control-panel">
        <form onSubmit={handleSubmit} className="visualization-form">
          <div className="form-group">
            <label htmlFor="model-select">Select Model:</label>
            <select
              id="model-select"
              value={selectedModel}
              onChange={handleModelChange}
              required
              disabled={loading.processing}
            >
              <option value="">-- Select a Model --</option>
              {Object.keys(models).map(model => (
                <option key={model} value={model}>{model}</option>
              ))}
            </select>
          </div>

          {selectedModel && (
            <div className="form-group">
              <label htmlFor="layer-select">Select Layer:</label>
              <select
                id="layer-select"
                value={selectedLayer}
                onChange={(e) => setSelectedLayer(e.target.value)}
                required
                disabled={loading.processing}
              >
                <option value="">-- Select a Layer --</option>
                {models[selectedModel].map(layer => (
                  <option key={layer} value={layer}>{layer}</option>
                ))}
              </select>
            </div>
          )}

          <div className="form-group checkbox-group">
            <input
              type="checkbox"
              id="heatmap-checkbox"
              checked={includeHeatmap}
              onChange={(e) => setIncludeHeatmap(e.target.checked)}
              disabled={loading.processing}
            />
            <label htmlFor="heatmap-checkbox">Include Grad-CAM Heatmap</label>
          </div>

          <div className="form-group">
            <label htmlFor="image-upload">Upload Image:</label>
            <input
              id="image-upload"
              type="file"
              accept="image/*"
              onChange={handleFileChange}
              required
              disabled={loading.processing}
            />
            {previewImage && (
              <div className="image-preview">
                <img src={previewImage} alt="Preview" />
              </div>
            )}
          </div>

          <button 
            type="submit" 
            className="visualize-button"
            disabled={loading.processing || !selectedModel || !selectedLayer || !image}
          >
            {loading.processing ? (
              <>
                <span className="button-spinner"></span>
                Processing...
              </>
            ) : (
              'Visualize'
            )}
          </button>
        </form>

        {error && !loading.processing && (
          <div className="form-error">
            <p>{error}</p>
          </div>
        )}
      </div>

      {results && (
        <div className="results-section">
          <h2>Visualization Results</h2>
          <div className="results-grid">
            <div className="result-card">
              <h3>Original Image</h3>
              <div className="result-image-container">
                <img 
                  src={`data:image/png;base64,${results.original}`} 
                  alt="Original"
                />
              </div>
            </div>
            
            <div className="result-card">
              <h3>Filter Output</h3>
              <div className="result-image-container">
                <img 
                  src={`data:image/png;base64,${results.filter}`} 
                  alt="Filter Output"
                />
              </div>
            </div>

            {results.heatmap && (
              <div className="result-card">
                <h3>Grad-CAM Heatmap</h3>
                <div className="result-image-container">
                  <img 
                    src={`data:image/png;base64,${results.heatmap}`} 
                    alt="Grad-CAM Heatmap"
                  />
                </div>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default FeatureMaps;
