import React, { useState, useEffect } from 'react';
import './FeatureMaps.css';

function FeatureMaps() {
  const [models, setModels] = useState({});
  const [selectedModel, setSelectedModel] = useState('');
  const [selectedLayer, setSelectedLayer] = useState('');
  const [includeHeatmap, setIncludeHeatmap] = useState(true);
  const [image, setImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(false);
  const [loadingModels, setLoadingModels] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchModels = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/models');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setModels(data);
        setLoadingModels(false);
      } catch (err) {
        console.error('Error fetching models:', err);
        setError('Failed to load models. Please try again later.');
        setLoadingModels(false);
      }
    };

    fetchModels();
  }, []);

  const handleModelChange = (e) => {
    setSelectedModel(e.target.value);
    setSelectedLayer('');
    setResults(null); // Clear previous results when model changes
  };

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setImage(file);
      
      // Create preview URL
      const reader = new FileReader();
      reader.onload = (event) => {
        setImagePreview(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!selectedModel || !selectedLayer || !image) {
      setError('Please select a model, layer, and image');
      return;
    }

    setLoading(true);
    setError('');
    setResults(null);

    try {
      const formData = new FormData();
      formData.append('model', selectedModel);
      formData.append('layer', selectedLayer);
      formData.append('heatmap', includeHeatmap);
      formData.append('file', image);

      const response = await fetch('http://localhost:5000/api/process', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Processing failed');
      }

      const data = await response.json();
      setResults(data);
    } catch (err) {
      console.error('Error:', err);
      setError(err.message || 'An error occurred during processing');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="feature-maps-container">
      <header className="header">
        <h1>CNN Visualization Tool</h1>
        <p>Visualize feature maps and Grad-CAM heatmaps from popular CNN models</p>
      </header>

      <div className="main-content">
        <form onSubmit={handleSubmit} className="visualization-form">
          <div className="form-section">
            <h2>Model Configuration</h2>
            
            <div className="form-group">
              <label htmlFor="model-select">Select Model:</label>
              {loadingModels ? (
                <div className="loading-text">Loading models...</div>
              ) : (
                <select
                  id="model-select"
                  value={selectedModel}
                  onChange={handleModelChange}
                  required
                  disabled={loadingModels}
                >
                  <option value="">-- Select a Model --</option>
                  {Object.keys(models).map(model => (
                    <option key={model} value={model}>{model}</option>
                  ))}
                </select>
              )}
            </div>

            {selectedModel && (
              <div className="form-group">
                <label htmlFor="layer-select">Select Layer:</label>
                <select
                  id="layer-select"
                  value={selectedLayer}
                  onChange={(e) => setSelectedLayer(e.target.value)}
                  required
                  disabled={loading}
                >
                  <option value="">-- Select a Layer --</option>
                  {models[selectedModel].map(layer => (
                    <option key={layer} value={layer}>{layer}</option>
                  ))}
                </select>
              </div>
            )}

            <div className="form-group checkbox-group">
              <label>
                <input
                  type="checkbox"
                  checked={includeHeatmap}
                  onChange={(e) => setIncludeHeatmap(e.target.checked)}
                  disabled={loading}
                />
                Include Grad-CAM Heatmap
              </label>
            </div>
          </div>

          <div className="form-section">
            <h2>Image Input</h2>
            
            <div className="form-group">
              <label htmlFor="image-upload">Upload Image:</label>
              <input
                id="image-upload"
                type="file"
                accept="image/*"
                onChange={handleFileChange}
                required
                disabled={loading}
              />
            </div>

            {imagePreview && (
              <div className="image-preview">
                <h3>Preview:</h3>
                <img src={imagePreview} alt="Upload preview" />
              </div>
            )}
          </div>

          <div className="form-actions">
            <button
              type="submit"
              disabled={loading || loadingModels}
              className="submit-button"
            >
              {loading ? (
                <>
                  <span className="spinner"></span>
                  Processing...
                </>
              ) : (
                'Visualize'
              )}
            </button>
          </div>

          {error && (
            <div className="error-message">
              <span className="error-icon">⚠️</span>
              {error}
            </div>
          )}
        </form>

        {results && (
          <div className="results-section">
            <h2>Visualization Results</h2>
            
            <div className="results-grid">
              <div className="result-card">
                <h3>Original Image</h3>
                <div className="image-wrapper">
                  <img
                    src={`data:image/png;base64,${results.original}`}
                    alt="Original"
                  />
                </div>
              </div>
              
              <div className="result-card">
                <h3>Filter Output</h3>
                <div className="image-wrapper">
                  <img
                    src={`data:image/png;base64,${results.filter}`}
                    alt="Filter Output"
                  />
                </div>
              </div>

              {results.heatmap && (
                <div className="result-card">
                  <h3>Grad-CAM Heatmap</h3>
                  <div className="image-wrapper">
                    <img
                      src={`data:image/png;base64,${results.heatmap}`}
                      alt="Grad-CAM Heatmap"
                    />
                  </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default FeatureMaps;
